webpackJsonp([3,192],{619:function(n,t){n.exports={rawContent:"\n<canvas id=\"canvas\"></canvas>\n\n与三年未见的同学说一声再见\n\n第一次在颐和园中游荡\n\n回到了三年前来北京蹭饭的地方\n\n夜里再次来到北医三院的急诊\n\n没能去学校的游泳馆游泳\n\n<style>\n#canvas {\n  position: absolute;\n  top: 0;\n  left: 0;\n  z-index: 0;\n}\n\np {\n  color: white;\n  z-index: 1;\n  position: relative;\n  text-align: center;\n  animation: change 10s infinite alternate;\n}\n\n@keyframes change {\n  0% {\n    color: hsla(0, 100%, 100%, 1);\n  }\n  100% {\n    color: hsla(180, 0%, 0%, 1);\n  }\n}\n</style>\n\n<!--script type=\"text/javascript\">\nlet canvas = document.getElementById('canvas')\ncanvas.height = window.innerHeight\ncanvas.width = window.innerWidth\nlet ctx = canvas.getContext('2d')\n\nlet g = 0.09\nlet startX = canvas.width / 2\nlet startY = canvas.height / 2\nlet lifeMax = 50\n\nfunction Sand(x, y) {\n  this.x = x\n  this.y = y\n  this.vx = Math.random() * 5 - 2.5\n  this.vy = Math.random() * 5 - 2.5\n  this.ay = g\n  this.size = 2\n  this.life = lifeMax\n}\n\nSand.prototype.draw = function() {\n  ctx.fillStyle = `hsla(${Math.floor(this.life / lifeMax * 60)}, 100%, ${Math.floor(this.life / lifeMax * 70) + 30}%, 0.8)`\n  ctx.fillRect(this.x, this.y, this.size, this.size)\n}\n\nSand.prototype.step = function() {\n  this.x += this.vx\n  this.y += this.vy\n  this.vy += this.ay\n  this.life--\n}\n\nlet clearWorld = function() {\n  ctx.fillStyle = 'rgba(0,0,0,0.2)'\n  ctx.fillRect(0, 0, canvas.width, canvas.height)\n}\n\nlet count = 1000\nlet sands = []\nfor (let i = 0; i < count; i++) {\n  setTimeout(function() {\n    sands.push(new Sand(canvas.width / 2, canvas.height / 2))\n  }, Math.random() * 3000)\n}\n\nfunction loop() {\n  requestAnimationFrame(loop)\n  clearWorld()\n  for (let sand of sands) {\n    sand.step()\n    if (sand.life <= 0) {\n      sands.shift()\n      sands.push(new Sand(startX, startY))\n    }\n    sand.draw()\n  }\n}\n\nrequestAnimationFrame(loop)\n\nfunction redraw(event) {\n  direction = Math.random() * Math.PI * 2\n  startX = event.clientX\n  startY = event.clientY\n}\n\nwindow.addEventListener('mousedown', redraw)\n\nwindow.addEventListener('resize', resizeCanvas)\n\nfunction　 resizeCanvas() {\n  canvas.width = window.innerWidth\n  canvas.height = window.innerHeight\n}\n\nlet direction = Math.random() * 2 * Math.PI\n\nfunction moveOrigin() {\n  requestAnimationFrame(moveOrigin)\n  startX += Math.cos(direction) * 10\n  startY += Math.sin(direction) * 10\n  if (startY > canvas.height || startY < 0) {\n    direction = -direction\n  } else if (startX > canvas.width || startX < 0) {\n    direction = Math.PI - direction\n  }\n  direction += (Math.random() * 0.4 - 0.2)\n}\n\nrequestAnimationFrame(moveOrigin)\n</script-->\n",metaData:{layout:"post",title:"Goodbye",excerpt:"to ZN",category:"life",tags:["life","ES2015"],disqus:!0}}}});